<script>import { forwardEventHandlers, T } from '@threlte/core';
import { Audio as ThreeAudio } from 'three';
import { useAudio } from '../utils/useAudio';
import { useThrelteAudio } from '../useThrelteAudio';
export let src;
export let id = undefined;
export let volume = undefined;
export let playbackRate = undefined;
export let autoplay = undefined;
export let detune = undefined;
export let loop = undefined;
const { getAudioListener } = useThrelteAudio();
const listener = getAudioListener(id);
if (!listener) {
    throw new Error(`No Audiolistener with id ${id} found.`);
}
export const ref = new ThreeAudio(listener);
const { pause, play, stop, setAutoPlay, setDetune, setLoop, setPlaybackRate, setSrc, setVolume } = useAudio(ref);
export { play, pause, stop };
$: setAutoPlay(autoplay);
$: setSrc(src);
$: setVolume(volume);
$: setPlaybackRate(playbackRate);
$: setLoop(loop);
$: setDetune(detune);
const component = forwardEventHandlers();
</script>

<T is={ref} {...$$restProps} let:ref bind:this={$component}>
  <slot {ref} />
</T>
